mapper.select:
select * from USER_LEAVE_SETTING WHERE 1=1

	and state=#{state}


	and ORG_id = #{orgId}


	and user_id = #{userId}


	and (start_time >=#{time} and end_time <=#{time} )


	limit #{offset}, #{limit}

select * from USER_LEAVE_SETTING WHERE 1=1 and state =1 
and org_id in (select id from BASE_APP_ORGAN start with id= #{orgId} connect by prior id = parent_id)
and user_id not in 
(

		select LEADER_ID from  USER_LEADER_ACCESS_STATE  where state=1 and LEADER_ID not in (
			select LEADER_ID from  USER_LEADER_ACCESS_STATE  
			where state=1  and user_id =#{userId}
		)
		and LEADER_ID!=#{userId}
)
and to_date(start_time,'yyyy-mm-dd')<=#{currentDate}
and to_date(end_time,'yyyy-mm-dd')>=#{currentDate}

	limit #{offset}, #{limit}

select count(*) from USER_LEAVE_SETTING WHERE 1=1 and state =1 
and org_id in (select id from BASE_APP_ORGAN start with id= #{orgId} connect by prior id = parent_id)
and user_id not in 
(

		select LEADER_ID from  USER_LEADER_ACCESS_STATE  where state=1 and LEADER_ID not in (
			select LEADER_ID from  USER_LEADER_ACCESS_STATE  
			where state=1  and user_id =#{userId}
		)
		and LEADER_ID!=#{userId}
)
and to_date(start_time,'yyyy-mm-dd')<=#{currentDate}
and to_date(end_time,'yyyy-mm-dd')>=#{currentDate}
